// Math Properties:
console.log(Math.PI); //3.141592653589793
console.log(Math.trunc(Math.PI)) // 3
console.log(Math.round(3.9)) // 4
console.log(Math.ceil(4.1)); // 5 
console.log(Math.floor(4.9)); //4
console.log(Math.pow(2,4)); // 16
console.log(Math.min(89,90)); //89
console.log(Math.max(-1,9)); //9
console.log(Math.random()) // 0.30044538876259164 - any random value between 0 - 1

// To generate 1 - 10 randomly
console.log(Math.ceil(Math.random()*10));
//if we need to generate 0-10 use floor and + 1 with answer










